// declarations.d.ts

declare module "*.svg" {
    const content: string;
    export default content;
}

declare module "*.json" {
    const value: any;
    export default value;
}

declare module "*.jpg" {
    const value: any;
    export default value;
}

declare module "*.png" {
    const value: any;
    export default value;
}
